- (BOOL)shouldPollToUpdateString:(NSString *)inString
{
	NSEnumerator	*enumerator;
	NSString		*stringRequiringPolling;
	BOOL			shouldPoll = NO;

	enumerator = [stringsRequiringPolling objectEnumerator];
	while ((stringRequiringPolling = [enumerator nextObject])) {
		if ([inString rangeOfString:stringRequiringPolling].location != NSNotFound) {
			NSNumber *hasPollingTime = [pollingTimeDict valueForKey:stringRequiringPolling];
			if (hasPollingTime) {
				if ([hasPollingTime boolValue]) {
					[pollingTimeDict setValue:[NSNumber numberWithBool:NO] forKey:stringRequiringPolling];
					shouldPoll = YES;
					break;
				} else {
					continue;
				}
			} else {
				shouldPoll = YES;
				break;
			}
		}
	}

	return shouldPoll;
}

