- (unsigned int) findBlockRangeAtIndex: (unsigned int) index inString: (NSString *) aString
{
	int j;
	int listLength = 1;

	//Need error checking

	for (; index + listLength < [aString length]; listLength++)
	{
		if ([aString characterAtIndex: index + listLength] == 'i')
		{
			for (j = 1; j < [aString length]; j++)
			{
				if ([aString characterAtIndex: index + j + listLength] == 'e') break;
			}
			listLength += j;
		} else if (isdigit([aString characterAtIndex: index + listLength]))
		{
			NSRange range;
			for (j = 0; j < [aString length]; j++)
			{
				if (isdigit([aString characterAtIndex: (j + index + listLength)]) && [aString characterAtIndex: (j + index + 1 + listLength)] == ':')
				{
					range.location = index + listLength;
					range.length = j + 1;
					break;
				}
			}
			listLength += j + 1;
			j = [[aString substringWithRange: range] intValue];
			listLength += j;
		} else if ([aString characterAtIndex: index + listLength] == 'l')
		{
			listLength += [self findBlockRangeAtIndex: (index + listLength) inString: aString] - 1;
		} else if ([aString characterAtIndex: index + listLength] == 'd')
		{
			listLength += [self findBlockRangeAtIndex: (index + listLength) inString: aString] - 1;
		} else if ([aString characterAtIndex: index + listLength] == 'e')
		{
			break;
		}
	}
	return listLength + 1;
}

- (NSMutableArray *) decodeListFromString: (NSString *) aString withRange: (NSRange) range
{
	//Insert error checking

	NSMutableArray *methodArray = [[NSMutableArray alloc] init];

	unsigned int j = 0;
	unsigned int k = range.length + range.location;
	for (int i = range.location; i <= k; i++)
	{
		if ([aString characterAtIndex: i] == 'i')
		{
			for (j = 1; j < [aString length]; j++)
			{
				if ([aString characterAtIndex: i + j] == 'e') break;
			}
			range.location = i + 1;
			range.length = j - 1;
			[methodArray addObject: [self decodeIntegerFromString: aString withRange: range]];
			i += j;
		} else if (isdigit([aString characterAtIndex: i]))
		{
			for (j = 0; j < [aString length]; j++)
			{
				if (isdigit([aString characterAtIndex: (j + i)]) && [aString characterAtIndex: (j + i + 1)] == ':')
				{
					range.location = i;
					range.length = j + 1;
					break;
				}
			}
			i += range.length;
			j = [[aString substringWithRange: range] intValue];
			range.location += range.length + 1;
			range.length = j;
			[methodArray addObject: [self decodeStringFromString: aString withRange: range]];
			i += range.length;
		} else if ([aString characterAtIndex: i] == 'l')
		{
			range.location = i + 1;
			range.length = [self findBlockRangeAtIndex: i inString: aString] - 2;
			[methodArray addObject: [self decodeListFromString: aString withRange: range]];
			i += range.length + 1;
		} else if ([aString characterAtIndex: i] == 'd')
		{
			range.location = i + 1;
			range.length = [self findBlockRangeAtIndex: i inString: aString] - 2;
			[methodArray addObject: [self decodeDictionaryFromString: aString withRange: range]];
			i += range.length + 1; 
		}
	}

	return methodArray;
}



