data MpdStatus = MpdStatus {
  volume      :: Integer,
  crossfade   :: Integer,
  isRepeating :: Bool,
  isRandom    :: Bool
} deriving Show

emptyMpdStatus = MpdStatus { volume = 0,
                             crossfade = 0,
                             isRepeating = False,
                             isRandom = False }

buildMpdStatus :: [String] -> MpdStatus
buildMpdStatus xs = buildMpdStatus' xs emptyMpdStatus
  where buildMpdStatus' [] accum = accum
        buildMpdStatus' (line:lines) accum =
          let (key, _:_:value) = break (== ':') line
          in buildMpdStatus' lines $ accum (
           case key of
            "volume"    -> { volume = read value }
            "crossfade" -> { crossfade = read value }
            "repeat"    -> { isRepeating = (value == "1") }
            "random"    -> { isRandom = (value == "1") }
            _           -> {}
          )

