}

-(IBAction)checkNow:(id)sender
{
	if ([progessBar isHidden])
	{
		[progessBar setHidden:NO];
		[progessBar setIndeterminate:YES];
		[progessBar startAnimation:sender];
	}

	if (VAR_ISTYPE(sender, CFRunLoopTimer) or ![sender tag])
	{
		CFBundleRef bundle=CFBundleCreate(NULL, (CFURLRef)[NSURL fileURLWithPath:[apeBundle bundlePath]]);

		if (bundle)
		{
			[checkNow setEnabled:NO];
			OSStatus err=UNVC_RunUpdater(bundle, (CFStringRef)apeBundleID, &launchedUpdater);
			if (!err)
			{
				if (!VAR_ISTYPE(sender, CFRunLoopTimer))
				{
					[sender setTag:1];
					[sender setTitle:NSLocalizedString_(@"Cancel", "Cancel Button Title")];
				}
				CFPreferencesSetAppValue(CFSTR("SULastCheckDate"), (CFDateRef)[NSDate date],  (CFStringRef)apeBundleID);
				CFPreferencesAppSynchronize((CFStringRef)apeBundleID);
			}
			else
			{
				NSString* errorString=[NSString stringWithFormat:@"Failed. Error %d. %s", err,  GetMacOSStatusCommentString ? GetMacOSStatusCommentString(err) : ""];
				[lastStatus setStringValue:[NSString stringWithFormat: lastStatusString, GetNSStringFromDate((CFDateRef)[NSDate date], kCFDateFormatterShortStyle,kCFDateFormatterShortStyle), errorString]];
				[lastStatus setToolTip:[NSString stringWithFormat: lastStatusString, GetNSStringFromDate((CFDateRef)[NSDate date], kCFDateFormatterFullStyle,kCFDateFormatterFullStyle), errorString]];
			}
			[checkNow setEnabled:YES];			
			CFRelease(bundle);
		}



	}
	else
	{
		if ([checkingTimer isValid])
			[checkingTimer invalidate];
		[checkingTimer release];
		checkingTimer=NULL;
		[sender setEnabled:NO];
		[[NSDistributedNotificationCenter defaultCenter] postNotificationName:@"com.unsanity.updater.CancelUpdate" object:apeBundleID userInfo:NULL deliverImmediately:YES];
		[NSTimer scheduledTimerWithTimeInterval:2.0 target:self selector:@selector(updateCheckComplete:) userInfo:NULL repeats:NO];

	}
}

