/*cette fonction pour a partir d'une case, soustrait a l'ensemble des valeurs possible des cases non affécté et lié a la case en question la valeur de cette deniére
le tableau domaine etant un tableua de 81 tableau d'entier, c'est tableau d'entier de 9 chiffre contine chaqun 0 ou 1 en fonction de la presence de la valeur en fonction
de sa presence dans l'enesmble de valeurs possible de la case(*/
int tri_domaine(int *Sudoku,int Case,int **Domaine)
{
	int j = 0,i,k,k1,k2;
	while(j<9)
	{
	    /* pour chaque case non affécté de la region de la case considéré, on sosutrait sa valeur a son ensemble de valeur possible */
		if(Sudoku[region[region2[Case]][j]] == 0)
		{
			Domaine[region[region2[Case]][j]][Sudoku[Case]-1] = 0;
		}
		/* pour chaque case non affécté de la ligne de la case considéré, soustrait sa valeur a son ensemble de valeur possible */
		if(Sudoku[(Case/9)*9 + j] == 0)
		{
			Domaine[(Case/9)*9 + j][Sudoku[Case]-1] = 0;
		}
				/* pour chaque case non affécté de la colonnee de la case considéré, on soustrait sa valeur a son ensemble de valeur possible */
		if(Sudoku[Case%9 + j*9] == 0)
		{
			Domaine[Case%9 + j*9][Sudoku[Case]-1] = 0;
		}
		i = 0;
		k = k1 = k2 = 0;
		/* puis pour chaque case dont on a soustré une valeur, on verifi que son ensemble de valeur posible n'est pas vide */
		while(i<9)
		{
			if(Domaine[region[region2[Case]][j]][i] == 1) k++;
			if(Domaine[(Case/9)*9 + j][i] == 1) k1++;
			if(Domaine[Case%9 + j*9][i] == 1) k2++;
			i++;
		}
		/* si c'est le cas on renvoi 0 */
		if ((k == 0) || (k1 == 0) || (k2 == 0)) return 0;			
		j++;
	}
	return 1;
}
/* cré un domaine de valeur pour les cases d'un sudoku considéré */ 
int Creation_domaine(int *Sudoku,int **Domaine)
{
	int i=0,j;
/* on initialise toute les valeur a 1(aucune d'elle n'est soustraite de l'emsemble de valeur possible de chaque case) */
	while(i<81)
	{
		j = 0;
		while(j<9)
		{
			Domaine[i][j] = 1;
			j++;
		}
		i++;
	}
	i=0;
	while(i<81)
	{
	/* puis pour chaque case ayant une vlauer fixé par l'enoncé, on tri les domaine de definition de chaque case qui lui sont lié*/
		if(Sudoku[i] != 0) 
		{
			tri_domaine(Sudoku,i,Domaine);
		}
		i++;
	}
	return 1;
}

int Resolution_Forward_checking(int *Sudoku,int **Domaine,int prof)
{
	int i = 1,temp[81],**temp_dom,j=0;
	/* si la profondeur est de 81 on a affécté une valeur pour les 81 case du sudoku, celui ci contien donc uen affectation total */
	if(prof == 81) return 1;
	/*  si la case a deja une valeur qui lui est afécté (par l'enoncé) on continue  a la case suivante */
	if(Sudoku[prof] != 0) return Resolution_Forward_checking(Sudoku,Domaine,prof +1);
	/* allocation de la mémoire */
	temp_dom = (int**)calloc(81,sizeof(int*));
	while(j<81)
	{
		temp_dom[j] = (int*)calloc(9,sizeof(int));
		j++;
	}
	j= 0;
	/* pour chaque valeur entr 1 est 9*/
	while(i<=9)
	{	
	/* on sauvegarde, l'affectation, et le domaine de definition tel qu'ils sont */
		memcpy(temp,Sudoku,81*sizeof(int));
		while(j<81)
		{
			memcpy(temp_dom[j],Domaine[j],9*sizeof(int));
			j++;
		}
		/* si la valeur est dans l'ensemble de valuer possible de la case "prof*/
		if(Domaine[prof][i-1] == 1)
		{
		/* on lui affecte cette valeur */
			temp[prof] = i;
			/* puis on tri de maniére a encore reduire lensemble de valeur de diférnete case, si un emsemble dviens vide, alors on passe a une valeur suivante, celle la ne corespond a aucune solution*/
			if(!tri_domaine(temp,prof,temp_dom)) i++;
			/* puis on verifie de maniére recursive que l'affectation est bien valide */
			if(Resolution_Forward_checking(temp,temp_dom,prof+1) )
			{
			/* si c'est le cas on sauvergarde le sudoky trouvé et on renvoie 1 */
				memcpy(Sudoku,temp,81*sizeof(int));
				return 1;
			}
		}
		i++;
	}
	/* si aucune valeur ne corespond la fonction renvoi 0 et fait donc un retour en arriére */
	return 0;
}
/* fonction principale */
main()
{
/* on defini, alous la mémoire initialise le sudoku  le saisie, le resou, puis l'affiche */
	int j=0,Sudoku[81],**Domaine;
	Domaine = (int**)calloc(81,sizeof(int*));
