#include <stdio.h>
#include <math.h>

#define x 0
#define y 1
#define a 0
#define b 1
#define c 2
#define d 3

typedef double point[2];
typedef double quadrilateral[4][2];

double *Midpoint(point first, point second)
{
	point midpoint;
	midpoint[x] = (second[x] + first[x]) / 2;
	midpoint[y] = (second[y] + first[y]) / 2;
	return midpoint;
}


double Quadrance(point first, point second)
{

	double xDifference = second[x] - first[x], yDifference = second[y] - first[y];
	return (xDifference * xDifference) + (yDifference * yDifference);
}

double Distance(point first, point second)
{
	return sqrt(Quadrance(first, second));
}

double Slope(point first, point second)
{
	return (second[y] - first[y]) / (second[x] - first[x]);
}

int main(void)
{
	quadrilateral foo;
	char *result;
	printf("Enter first point: ");
	scanf("%f, %f", &foo[a][x], &foo[a][y]);
	printf("Enter second point: ");
	scanf("%f, %f", &foo[b][x], &foo[b][y]);
	printf("Enter third point: ");
	scanf("%f, %f", &foo[c][x], &foo[c][y]);
	printf("Enter last point: ");
	scanf("%f, %f", &foo[d][x], &foo[d][y]);
	double *firstMidpoint = Midpoint(foo[a], foo[c]);
	double *secondMidpoint = Midpoint(foo[b], foo[d]);
	if (firstMidpoint[x] == secondMidpoint[x] && firstMidpoint[y] == secondMidpoint[y]) {
		if (Distance(foo[a], foo[c]) == Distance(foo[b], foo[d])) {
			if (Slope(foo[a], foo[c]) * Slope(foo[b], foo[d]) == -1.0) {
				result = "Square";
			} else {
				result = "Rectangle";
			}
		} else {
			if (Slope(foo[a], foo[c]) * Slope(foo[b], foo[d]) == -1.0) {
				result = "Rhombus";
			} else {
				result = "Parallelogram";
			}
		}
	} else {
		if (Slope(foo[a], foo[b]) == Slope(foo[c], foo[d]) || Slope(foo[a], foo[d]) == Slope(foo[b], foo[c])) {
			result = "Trapezoid";
		} else if (Slope(foo[a], foo[c]) * Slope(foo[b], foo[d]) == -1.0 && Distance(foo[a], foo[b]) + Distance(foo[c], foo[d]) == Distance(foo[a], foo[d]) + Distance(foo[b], foo[c])) {
			result = "Kite";
		}
	}
	printf("%s\n", result);
	return 0;
}

