cairo_status_t
_cairo_surface_get_extents (cairo_surface_t   *surface,
			    cairo_rectangle_t *rectangle)
{
    cairo_status_t status;
    double tx, ty; /* surface coordinates */

    if (surface->status)
	return surface->status;

    if (surface->finished)
	return CAIRO_STATUS_SURFACE_FINISHED;

    status = surface->backend->get_extents (surface, rectangle);

    /* get_extents returns coordinates in backend coordinates.
     * Therefore, we apply the device transformation instead of the inverse
     * transform.
     */
    tx = rectangle.x;
    ty = rectangle.y;
    cairo_matrix_transform_point (&surface->device_transform, &tx, &ty);

    /* All drawing is clipped to the backend size, so width and height may
     * need to be adjusted. Width and height are always the difference between
     * the backend dimensions and the topleft corner after transformation.
     */
    rectangle->x = (short)tx;
    rectangle->y = (short)ty;
    rectangle->width = rectangle->width - rectangle->x;
    rectangle->height = rectangle->height - rectangle->y;

    return status;
}

cairo_status_t
_cairo_surface_get_extents (cairo_surface_t   *surface,
			    cairo_rectangle_t *rectangle)
{
    cairo_status_t status;
    double tx, ty, twidth, theight; /* transformed coords */

    if (surface->status)
	return surface->status;

    if (surface->finished)
	return CAIRO_STATUS_SURFACE_FINISHED;

    status = surface->backend->get_extents (surface, rectangle);

    /* get_extents returns coordinates in backend coordinates.
     * Therefore, we apply the device transformation instead of the inverse
     * transform.
     */
    tx = rectangle.x;
    ty = rectangle.y;
    twidth = rectangle.width;
    theight = rectangle.height;
    _cairo_matrix_transform_bounding_box (&surface->device_transform,
					  &tx, &ty, &twidth, &theight);

    /* All drawing is clipped to the backend size, so width and height may
     * need to be adjusted. Width and height are always the difference between
     * the real dimensions and the topleft corner after transformation.
     */
    rectangle->x = (short)tx;
    rectangle->y = (short)ty;
    rectangle->width = rectangle->width - rectangle->x;
    rectangle->height = rectangle->height - rectangle->y;

    return status;
}

/*
-- Haskell version:
quicksort [] = []
quicksort (s:xs) = quicksort [x|x <- xs,x < s]
                   ++ [s]
                   ++ quicksort [x|x <- xs,x >= s]
*/

/* C version
   Using garbage collection (boehm) and a list library (glibc, syntax
   errors abound).
   list_filter_less_than/...greater_than are necessary because of the
   lack of closures.
 */
list *quicksort (list *l) {
     if (list_empty(l)) {
          return l;
     } else {
          value s = l->head;
          return list_append( quicksort( list_filter_less_than(l, s) ),
                              list_append( make_list(s),
                                           quicksort( list_filter_greater_than(l, s) )));
     }
}

id Silk_FontWithFamily_traits_size(id self, SEL selector, CFStringRef familyName, unsigned int traits, float size)
{
    id font=NULL;

    if (!gWebKitFontCache)
        gWebKitFontCache=CFDictionaryCreateMutable(NULL, 0, &kCFCopyStringDictionaryKeyCallBacks, &kCFTypeDictionaryValueCallBacks);


    CFStringRef foundName=NULL;

    if (gWebKitFontCache and gSubstitute and familyName and _CTFontDescriptorCopyAvailableFontFamilyNames!=NULL)
    {
        CFStringRef key=CFStringCreateWithFormat(NULL, NULL, CFSTR("%@"), familyName/*, traits*/);

        if (key)
        {
            foundName=CFDictionaryGetValue(gWebKitFontCache, familyName);

            if (!foundName)
            {
                CFArrayRef families=_CTFontDescriptorCopyAvailableFontFamilyNames();

                if (families and CFArrayGetCount(families))
                {

                    if (CFArrayContainsValue(families, CFRangeMake(0, CFArrayGetCount(families)), familyName))
                    {
                        foundName=familyName;

                    }

                    if (!foundName)
                    {
                        CFMutableArrayRef sortedFamilies=CFArrayCreateMutableCopy(NULL, 0, families);

                        if (sortedFamilies)
                        {
                            CFArraySortValues(sortedFamilies, CFRangeMake(0, CFArrayGetCount(sortedFamilies)), (CFComparatorFunction)CFStringCompare, (void*)kCFCompareCaseInsensitive);

                            CFIndex index=CFArrayBSearchValues(sortedFamilies, CFRangeMake(0, CFArrayGetCount(sortedFamilies)), familyName, (CFComparatorFunction)CFStringCompare, (void*)kCFCompareCaseInsensitive);

                            if (index!=kCFNotFound and index<CFArrayGetCount(sortedFamilies))
                            {
                                foundName=CFArrayGetValueAtIndex(sortedFamilies, index);
                                if (CFStringCompare(foundName, familyName, kCFCompareCaseInsensitive)!=kCFCompareEqualTo)
                                {
                                    foundName=NULL;
                                }

                            }

                            CFRelease(sortedFamilies);
                        }
                    }

                    if (foundName /*and !CFEqual(foundName, familyName)*/)
                    {

                        foundName=Silk_CopyReplacementFontNameForName(foundName);

                        if (foundName and CTFontCreateWithName)
                        {
                            CTFontRef theFont=CTFontCreateWithName(foundName, size, NULL);

                            if (theFont)
                            {
                                CFStringRef newName=CTFontCopyFamilyName(theFont);

                                if (newName)
                                {
                                    if (key and gWebKitFontCache)
                                        CFDictionarySetValue(gWebKitFontCache, key, newName);
                                    CFRelease(foundName);
                                    foundName=newName;

                                }

                                CFRelease(theFont);
                            }
                                                  }


                    }
                    else if (foundName and CFEqual(foundName, familyName))
                    {
                        foundName=NULL;
                    }

                }





                if (families)
                    CFRelease(families);

            }
            else 
            {
                CFRetain(foundName); // for the release below.
            }

            CFRelease(key);
        }

    }

    font=gFontWithFamily_traits_size(self, selector, foundName ? foundName : familyName, traits, (gMinFontSizeEnforced and size<(float)gMinFontSize) ? (float)gMinFontSize : size);

    if (foundName)
        CFRelease(foundName);


    return font;
}

