// Replicate the Unix tool basename

#import <stdio.h>
#import <Foundation/NSString.h>
#import <Foundation/NSArray.h>
#import <Foundation/NSFileManager.h>
#import <Foundation/NSAutoreleasePool.h>
#import <Foundation/NSPathUtilities.h>
#import <Foundation/NSProcessInfo.h>

int main(int argc, char *argv[])
{
	NSAutoreleasePool	*pool = [[NSAutoreleasePool alloc] init];
	NSString			*programName, *strArgs, *suffix;
	NSMutableString		*basename;
	NSProcessInfo		*proc = [NSProcessInfo processInfo];
	NSArray				*args = [proc arguments];
	NSRange				substr;
	int					i;

	programName = [proc processName];

	if ([args count] < 2)
	{
		printf("usage:  %s string [suffix]\n", [programName cString]);
		printf("\t%s [-a] [-s suffix] string [...]\n", [programName cString]);
		return 1;
	}

	strArgs = [args componentsJoinedByString: @" "];

	substr = [strArgs rangeOfString: @"-s"];
	if (substr.location != NSNotFound)
	{
		suffix = [args objectAtIndex: 2];
		printf("** DEBUG: entered first if (substr.location != NSNotFound)\n** DEBUG: suffix == %s\n", [suffix cString]);

		for(i=0; i<[args count]; i++) { NSLog("%@", [args objectAtIndex: i]);  }

		for (i = 2; i < [args count] - 1; i++)
		{
			basename = [args objectAtIndex: (i + 1)];

			printf("** DEBUG: basename = [args objectAtIndex: (i + 1)]\n");

			NSLog("%@", [[args objectAtIndex: (i + 1)] className]);

			substr = [basename rangeOfString: suffix];
			if (substr.location != NSNotFound)
			{
				printf("** DEBUG: entered the second if (substr.location != NSNotFound)\n");

				[basename deleteCharactersInRange: substr];
			}
			printf("%s\n", [basename cString]);
		}
	}

	[pool release];
	return 0;
}

