//------------------------------------------------------------------------------------------
Boolean TOpenWSPuzzMgr::sWSPuzzFilter(AEDesc* theItem, void* info, 
							void* callBackUD,  NavFilterModes filterMode)
{
	Boolean display = true;
	NavFileOrFolderInfo *theInfo = (NavFileOrFolderInfo*)info;
	FSRef ref;

	if (theInfo->isFolder == false) // i'm only interested in folders & bundles.
		return false;

	OSErr err = AECoerceDesc(theItem, typeFSRef, theItem);
#pragma unused(err)
	if (AEGetDescData( theItem, &ref, sizeof(FSRef)) == noErr)
	{
		LSItemInfoRecord outInfo;
		memset(&outInfo, 0, sizeof(outInfo));

		if (LSCopyItemInfoForRef (&ref,  
				kLSRequestBasicFlagsOnly | kLSRequestExtension | kLSRequestTypeCreator, 
				&outInfo) == noErr)
		{
			Boolean isAlias = ( (outInfo.flags & kLSItemInfoIsAliasFile) != 0 );
			if (isAlias)
			{
				Boolean resolveAliasChains = true; // handle alias to an alias to....
				Boolean targetIsFolder;
				Boolean wasAliased;
				err = FSResolveAliasFileWithMountFlags(&ref, resolveAliasChains, 
									&targetIsFolder, &wasAliased, kResolveAliasFileNoUI);
//				err = FSResolveAliasFile(&ref, resolveAliasChains,
//									&targetIsFolder, &wasAliased);
				if (err != noErr)
					return false;  // unresolved alias.

				OSStatus status = noErr;
				status = LSCopyItemInfoForRef (&ref,  
							kLSRequestBasicFlagsOnly | kLSRequestExtension | kLSRequestTypeCreator, 
							&outInfo);
				if (status != noErr)
					return false;  // couldn't get info.
			}

			Boolean isPlainFile = ( (outInfo.flags & kLSItemInfoIsPlainFile) != 0 );
			if (isPlainFile)
				return false;  // alias might have returned a plain file.

			Boolean isSymLink = ( (outInfo.flags & kLSItemInfoIsSymlink) != 0 );
			if (isSymLink)
				return false;  // we ignore sym links.

			Boolean isInvisible = ( (outInfo.flags & kLSItemInfoIsInvisible) != 0 );
			if (isInvisible)
				return false;  // we ignore invisible files.

			Boolean isVolume = ( (outInfo.flags & kLSItemInfoIsVolume) != 0 );
			if (isVolume)
				return true;  // ok, you can look in other volumes.

			Boolean isContainer = ( (outInfo.flags & kLSItemInfoIsContainer) != 0 );
			if (isContainer)
			{
				Boolean isApplication = ( (outInfo.flags & kLSItemInfoIsApplication) != 0 );
				if (isApplication)
					return false; // bundled app					
				Boolean isPackage = ( (outInfo.flags & kLSItemInfoIsPackage) != 0 );
				if (!isPackage)
					return true; // it's a normal folder.
			}

			// ok, now I should be down to just document packages of some sort.
			CFStringRef itemUTI = NULL;
			if (outInfo.extension != NULL)
			{
				itemUTI = UTTypeCreatePreferredIdentifierForTag(
						kUTTagClassFilenameExtension, outInfo.extension, NULL);
				CFRelease(outInfo.extension);
			}
			else
			{
				CFStringRef typeString = UTCreateStringForOSType( outInfo.filetype);
				itemUTI = UTTypeCreatePreferredIdentifierForTag(
						kUTTagClassOSType, typeString, NULL);
				CFRelease(typeString);
			}

			if (itemUTI != NULL)
			{
				display = UTTypeEqual(itemUTI, UTI_wspuzz);
				CFRelease(itemUTI);
			}
		}
	}
	return display;
}

