int main( int iArgC, char **pszArgV )
{
  CHure         oHure;        // ~ Basisklasse.
  CEdelhure     oEdelhure;    // ~ Unterklasse.
  CHure         *pHure;       // Zeiger auf eine CHure(!!!).

  // Hier werden jeweils einfach die Ficken()-Funktionen der jeweiligen Klassen aufgerufen, ganz normal.
  oHure.Ficken();     // Von CHure.
  oEdelhure.Ficken(); // Von CEdelhure.

  pHure = &oHure;     // Zeiger auf die CHure "oHure" ausrichten.
  pHure->Ficken();    // Ficken() aufrufen.
                      // Mit virtual : Ruft Ficken()-Code der CHure auf.
                      // Kein virtual: Ruft Ficken()-Code der CHure auf.

  pHure = &oEdelhure; // Zeiger auf die CEdelhure "oEdelhure" ausrichten.
                      // Zwar darf pHure normal nur auf ne CHure zeigen, weil aber CEdelhure von ihr abgeleitet ist,
                      // geht das ebenfalls klar (macht ja Sinn, denn ne abgeleitete Klasse hat sowieso schonmal alles
                      // das, was die Basisklasse auch hat).
  pHure->Ficken();    // Ficken() aufrufen.
                      // Mit virtual : Ruft Ficken()-Code der CEdelhure(!!!) auf, weil erkannt wird, dass der Zeiger - obwohl
                      //               vom Typ CHure* - in Wirklichkeit auf ne CEdelhure zeigt.
                      // Ohne virtual: Ruft Ficken()-Code der CHure() auf, weil NICHT erkannt werden kann, um welche Hure
                      //               es sich wirklich handelt. DA DER ZEIGER vom Typ CHure* ist, wird davon ausgegangen,
                      //               dass er auch auf eine solche zeigt.

  return 0;
}

