using namespace std;

struct agenda {
    string nome;
    int telefone;
};



int main(int argc, char *argv[])
{
    agenda *lista;
    bool flag=1;
    int min,mid,max;
    string chave;
    lista = new agenda[MAX];

    for (int x=0; x<MAX; x++)
    {
       cout << "Nome(" << x+1 << "): "; cin >> lista[x].nome;
       cout << "Telefone(" << x+1 << "): "; cin >> lista[x].telefone;
    }

    cout << "Buscar por: ";
    cin << chave;

    min=0;
    max=MAX-1;
    flag = 1;
    while (flag)
    {
        mid=(min+max)/2;
        if (lista[mid].nome == chave)
        {
                cout << "Nome: " << lista[mid].nome << endl;
                cout << "Tel.: " << lista[mid].telefone << endl;
                flag = 0;
        }
        else 
        {
                if ((chave >= lista[min].nome) && (chave <= lista[mid].nome))
                { 
                       max = mid-1;
                } 
                else 
                {
                        min = mid+1;
                }      
        }

    }    

    system("PAUSE");	
    return 0;
}

// Three different POSIX happy allocators for C++ STL objects and some 
// measurements.


extern "C"
{
#include <sys/types.h>
#include <sys/mman.h>
}

#include <limits>
#include <vector>
#include <iostream>
#include <iterator>

namespace leimyalloc {
  template <typename T>
  class mmap_allocator {
  public:
    typedef size_t size_type;
    typedef ptrdiff_t difference_type;
    typedef T* pointer;
    typedef T const * const_pointer;
    typedef T& reference;
    typedef T const & const_reference;

    //rebind
    template <typename U>
    struct rebind {
      typedef mmap_allocator <U> other;
    };

    pointer address (reference value ) const {
      return &value;
    }

    const_pointer address (const_reference value) const {
      return &value;
    }

    mmap_allocator () throw () {}

    mmap_allocator ( mmap_allocator const  &) throw () {}

    template <typename U>
    mmap_allocator (mmap_allocator <U> const &) throw () {} 

    ~mmap_allocator() throw () {}

    size_type max_size () const throw() {
      return ::std::numeric_limits <size_type>::max() / sizeof(T);
    }

    pointer allocate (size_type num, void *  hint = 0) {
      pointer p = (pointer) ::mmap(hint, num, PROT_READ | PROT_WRITE, MAP_ANON, -1, 0);
      int * val = (int *)p;
      if(val && *val == -1)
	p = NULL;
      return p;
    }

    void construct (pointer p, const_reference value) {
      new((void *)p) T(value);  //placement new
    }

    void destroy (pointer p) {
      p->~T();
    }

    void deallocate (pointer p, size_type num) {
      ::munmap((caddr_t) p, num);
    }

  };

  template <typename T1, typename T2>
  bool operator == (mmap_allocator <T1> const &, mmap_allocator <T2> const &) throw () {
    return true;
  }

  template <typename T1, typename T2>
  bool operator != (mmap_allocator <T1> const &, mmap_allocator <T2> const &) throw () {
    return false;
  }

}

