procedure tcgarm.handle_load_store(list:TAsmList;op: tasmop;oppostfix : toppostfix;reg:tregister;ref: treference);
  var
    tmpreg,tmpreg2 : tregister;
    tmpref : treference;
    l : tasmlabel;
    so : tshifterop;
  begin
    tmpreg:=NR_NO;

    { Be sure to have a base register }
    if (ref.base=NR_NO) then
      begin
        if ref.shiftmode<>SM_None then
          internalerror(200308294);
        ref.base:=ref.index;
        ref.index:=NR_NO;
      end;

    { absolute symbols can't be handled directly, we've to store the symbol reference
      in the text segment and access it pc relative

      For now, we assume that references where base or index equals to PC are already
      relative, all other references are assumed to be absolute and thus they need
      to be handled extra.

      A proper solution would be to change refoptions to a set and store the information
      if the symbol is absolute or relative there.
    }

    if (assigned(ref.symbol) and
        not(is_pc(ref.base)) and
        not(is_pc(ref.index))
       ) or
       { [#xxx] isn't a valid address operand }
       ((ref.base=NR_NO) and (ref.index=NR_NO)) or
       (ref.offset<-4095) or
       (ref.offset>4095) or
       ((oppostfix in [PF_SB,PF_H,PF_SH]) and
        ((ref.offset<-255) or
         (ref.offset>255)
        )
       ) or
       ((op in [A_LDF,A_STF]) and
        ((ref.offset<-1020) or
         (ref.offset>1020) or
         { the usual pc relative symbol handling assumes possible offsets of +/- 4095 }
         assigned(ref.symbol)
        )
       ) then
      begin
        reference_reset(tmpref);

        { load symbol }
        tmpreg:=getintregister(list,OS_INT);
        if assigned(ref.symbol) then
          begin
            current_asmdata.getjumplabel(l);
            cg.a_label(current_procinfo.aktlocaldata,l);
            tmpref.symboldata:=current_procinfo.aktlocaldata.last;

            current_procinfo.aktlocaldata.concat(tai_const.create_sym_offset(ref.symbol,ref.offset));

            { load consts entry }
            tmpref.symbol:=l;
            tmpref.base:=NR_R15;
            list.concat(taicpu.op_reg_ref(A_LDR,tmpreg,tmpref));
          end
        else
          a_load_const_reg(list,OS_ADDR,ref.offset,tmpreg);

        if (ref.base<>NR_NO) then
          begin
            if ref.index<>NR_NO then
              begin
                list.concat(taicpu.op_reg_reg_reg(A_ADD,tmpreg,ref.base,tmpreg));
                ref.base:=tmpreg;
              end
            else
              begin
                ref.index:=tmpreg;
                ref.shiftimm:=0;
                ref.signindex:=1;
                ref.shiftmode:=SM_None;
              end;
          end
        else
          ref.base:=tmpreg;
        ref.offset:=0;
        ref.symbol:=nil;
      end;

    if (ref.base<>NR_NO) and (ref.index<>NR_NO) and (ref.offset<>0) then
      begin
        if tmpreg<>NR_NO then
          a_op_const_reg_reg(list,OP_ADD,OS_ADDR,ref.offset,tmpreg,tmpreg)
        else
          begin
            tmpreg:=getintregister(list,OS_ADDR);
            a_op_const_reg_reg(list,OP_ADD,OS_ADDR,ref.offset,ref.base,tmpreg);
            ref.base:=tmpreg;
          end;
        ref.offset:=0;
      end;

    { floating point operations have only limited references
      we expect here, that a base is already set }
    if (op in [A_LDF,A_STF]) and (ref.index<>NR_NO) then
      begin
        if ref.shiftmode<>SM_none then
          internalerror(200309121);
        if tmpreg<>NR_NO then
          begin
            if ref.base=tmpreg then
              begin
                if ref.signindex<0 then
                  list.concat(taicpu.op_reg_reg_reg(A_SUB,tmpreg,tmpreg,ref.index))
                else
                  list.concat(taicpu.op_reg_reg_reg(A_ADD,tmpreg,tmpreg,ref.index));
                ref.index:=NR_NO;
              end
            else
              begin
                if ref.index<>tmpreg then
                  internalerror(200403161);
                if ref.signindex<0 then
                  list.concat(taicpu.op_reg_reg_reg(A_SUB,tmpreg,ref.base,tmpreg))
                else
                  list.concat(taicpu.op_reg_reg_reg(A_ADD,tmpreg,ref.base,tmpreg));
                ref.base:=tmpreg;
                ref.index:=NR_NO;
              end;
          end
        else
          begin
            tmpreg:=getintregister(list,OS_ADDR);
            list.concat(taicpu.op_reg_reg_reg(A_ADD,tmpreg,ref.base,ref.index));
            ref.base:=tmpreg;
            ref.index:=NR_NO;
          end;
      end;
    if (op=A_STR) and (oppostfix=PF_H) then
      begin
        shifterop_reset(so);
        so.shiftmode:=SM_LSR;
        so.shiftimm:=8;;
        list.concat(taicpu.op_reg_ref(A_STRB,reg,ref));
        tmpreg:=NR_NO;
        tmpreg:=getintregister(list,OS_INT);
        list.concat(taicpu.op_reg_reg_shifterop(A_MOV,tmpreg,reg,so));
        inc(ref.offset);
        list.concat(taicpu.op_reg_ref(A_STRB,tmpreg,ref));
      end
    else
     if (op=A_STR) and (oppostfix=PF_None) then
        begin
          tmpreg:=NR_NO;
          shifterop_reset(so);
          so.shiftmode:=SM_LSR;
          so.shiftimm:=8;;

          list.concat(taicpu.op_reg_ref(A_STRB,reg,ref));

          tmpreg:=getintregister(list,OS_INT);
          list.concat(taicpu.op_reg_reg_shifterop(A_MOV,tmpreg,reg,so));
          inc(ref.offset);
          list.concat(taicpu.op_reg_ref(A_STRB,tmpreg,ref));

          list.concat(taicpu.op_reg_reg_shifterop(A_MOV,tmpreg,tmpreg,so));
          inc(ref.offset);
          list.concat(taicpu.op_reg_ref(A_STRB,tmpreg,ref));

          list.concat(taicpu.op_reg_reg_shifterop(A_MOV,tmpreg,tmpreg,so));
          inc(ref.offset);
          list.concat(taicpu.op_reg_ref(A_STRB,tmpreg,ref));
        end
      else
        if (op=A_LDR) and ((oppostfix=PF_SH) or (oppostfix=PF_H)) then
           begin
             shifterop_reset(so);
             so.shiftmode:=SM_LSL;
             so.shiftimm:=6;
             list.concat(taicpu.op_reg_ref(A_LDRB,reg,ref));
             tmpreg:=NR_NO;
             tmpreg:=getintregister(list,OS_INT);
             inc(ref.offset);
             if (oppostfix=PF_SH) then
                list.concat(taicpu.op_reg_ref(A_LDRSB,tmpreg,ref))
             else
                list.concat(taicpu.op_reg_ref(A_LDRB,tmpreg,ref));
             list.concat(taicpu.op_reg_reg_reg_shifterop(A_ORR,reg,reg,tmpreg,so));
           end
        else
          if (op=A_LDR) and (oppostfix=PF_None) then
             begin
               //roozbeh this seem to work,at least doesnt give acces violation when doing make
               //but uncommenting a_ldrb then gives error!
               shifterop_reset(so);so.shiftmode:=SM_LSL;so.shiftimm:=6;
               tmpreg:=getintregister(list,OS_INT);
               list.concat(taicpu.op_reg_ref(A_LDRB,reg,ref));
               inc(ref.offset);
               //list.concat(taicpu.op_reg_ref(A_LDRB,tmpreg,ref));
               tmpreg2:=getintregister(list,OS_INT);
               list.concat(taicpu.op_reg_reg_reg_shifterop(A_ORR,tmpreg2,reg,tmpreg,so));
               inc(ref.offset);
               //list.concat(taicpu.op_reg_ref(A_LDRB,reg,ref));
               so.shiftimm:=16;
               //list.concat(taicpu.op_reg_reg_reg_shifterop(A_ORR,tmpreg,tmpreg2,reg,so));
               //inc(ref.offset);
               //list.concat(taicpu.op_reg_ref(A_LDRB,tmpreg2,ref));
               so.shiftimm:=24;
               //list.concat(taicpu.op_reg_reg_reg_shifterop(A_ORR,reg,tmpreg,tmpreg2,so));
             end
          else
    list.concat(setoppostfix(taicpu.op_reg_ref(op,reg,ref),oppostfix));
  end;

