- (void) generateButtonPath;
{
    // recached the bezierPath...
    [disclosureTriangle release];
    disclosureTriangle = [[NSBezierPath bezierPath] retain];
    [disclosureTriangle moveToPoint:NSMakePoint( -4.0f, 5.0f )];
    [disclosureTriangle lineToPoint:NSMakePoint( -4.0f, -5.0f )];
    [disclosureTriangle lineToPoint:NSMakePoint( 5.0f, 0.0f )];
    [disclosureTriangle closePath];

    // apply a transformation to the path....
    NSAffineTransform *transform = [NSAffineTransform transform];
    [transform translateXBy:10.0f yBy:10.0f];
    [transform rotateByDegrees:( expanded ? 270.0f : 0.0f )];
    [disclosureTriangle transformUsingAffineTransform:transform];
}

