<?php
	class pop3
	{
		var $fp;
		var $user;
		var $password;
		var $messages;
		function connect ()
		{
			$this->fp = fsockopen($this->host, 110, $errno, $errstr, 30);
			if (!$this->fp) {
				return $errno;
			}
			else
			{
				$ok=0;
				while ((!feof($this->fp)) && ($ok < 3)) {
        			$sockread = fgets($this->fp, 128);
					$cmd=split(" ",$sockread);
					if ($cmd[0] == "+OK") 
					{
						$ok++;
						switch ($ok) 
						{	
							case 1:
								fwrite($this->fp, "user $this->user\r\n");
								break;
							case 2:
								fwrite($this->fp, "pass $this->password\r\n");
								break;
						}
					}

	    			}
				return 0;
			}
		}
		function stat_messages()
		{
			$ok=0;
			$this->messages = "";
			fwrite($this->fp, "STAT\r\n");
			while (!feof($this->fp) && ($ok != 1))
			{
       			$sockread = fgets($this->fp, 128);
				if (substr($sockread,0,3) == "+OK")
				{
					$msgs = split(" ",trim($sockread,"\n\r"));
					$ok=1;
				}
			}
			$this->messages=$msgs[1];
			return $msgs;
		}
		function retr_message($msg_num)
		{
			$ok=0;
			$headers = $this->headers_message($msg_num);
			$message = $this->get_message($msg_num,strlen($headers));
			$headers=split("\n",$headers);
			for ($x=0; $x <= count($headers)-1; $x++)
			{
				$hdr_atual = $headers[$x];
				if (strpos($hdr_atual,":") > 0)
				{
					$hdr[substr($hdr_atual,0,strpos($hdr_atual,":"))] = trim(substr($hdr_atual,strpos($hdr_atual,":") + 1,strlen($hdr_atual)));
				}
			}
			$hdr["message"]=$message;
			return $hdr;

		}
		function get_message($msg_num,$header_lenght)
		{
			$ok=0;
			$msg="";
			fwrite($this->fp, "RETR $msg_num 0\r\n");
			while (!feof($this->fp) && ($ok != 1))
			{
       			$sockread = fgets($this->fp, 128);
				if (substr($sockread,0,1) == ".") { $ok = 1; }
				else 
				{
					if (substr($sockread,0,3) != "+OK")
					{
						$msg = $msg . $sockread;
					}
				}
			}
			return substr($msg,$header_lenght,strlen($msg));
		}		
		function headers_message($msg_num)
		{
			$ok=0;
			$msg="";
			fwrite($this->fp, "TOP $msg_num 0\r\n");
			while (!feof($this->fp) && ($ok != 1))
			{
       			$sockread = fgets($this->fp, 128);
				if (substr($sockread,0,1) == ".") { $ok = 1; }
				else 
				{
					if (substr($sockread,0,3) != "+OK")
					{
						$msg = $msg . $sockread;
					}
				}
			}
			return $msg;
		}
		function disconnect () {
			fclose($this->fp);
		}
	}
?>


