}

void
runonstack(void (*fn)(void), char *stack)
{
       state = "in assembly";
       asm(
               "pushal\n"
               "movl 8(%ebp), %ebx\n"  /* ebx = fn */
               "movl 12(%ebp), %eax\n" /* ecx = stack */
               "xchgl %esp, %eax\n"
               "pushl %eax\n"
               "call *%ebx\n"
               "popl %esp\n"
               "popal\n"
       );
       state = "out of assembly";
}

int
main(int argc, char **argv)
{
       runonstack(hello, stack+sizeof stack);
}

