: add-meth-sig ( meth word -- bool )
    dup dup "signatures" word-prop [ nip ] [ { } clone dup -rot "signatures" set-word-prop ] if*
    rot add  ( word array )
    2dup "signatures" set-word-prop
    dupd make-send-quot 
    dupd define-compound
    try-compile
    t ;

: msg-name ( selector -- word-name )
    selector-name "[" swap "]" append3 ;

: make-send-error-quot ( selector-name -- quot )
    [ \ drop , ":" over member? [ \ >r ,  [ CHAR: : = ] subset [ \ drop , ] each \ r> , ] [ drop ] if ] [ ] make ;

: make-send-stub-quot ( meth -- quot )
    stub ;

: make-send-quot-empty ( word -- quot )
    [  \ 2dup , \ signature , dup  dup literalize  ,  \ add-meth-sig ,   f cons  ,
    word-name make-send-error-quot , \ if , ] [ ] make ;

: make-send-quot-long ( word meth-vector -- quot )
    [ over , \ selector , \ 2dup , \ signature , \ objc-method-types ,  
        [  
            [ \ dup , dup objc-method-types , \ = , ] [ ] make swap  
            \ drop make-send-stub-quot curry 
            2array 
        ] map swap make-send-quot-empty [ t ] swap 2array add , 
        \ cond , 
     ] [ ] make ;

: make-send-quot-short ( word meth -- quot )
