namespace zscheme {

	Number_impl *const Num_toolbox::ZERO = SmallInteger::zero();
	Number_impl *const Num_toolbox::ONE  = SmallInteger::one();

	Number_impl* Num_toolbox::toComplex(int n)
	{
		return new Complex(n);
	}	

	Number_impl* Num_toolbox::toComplex(double d)
	{
		return new Complex(d);
	}

	Number_impl* Num_toolbox::toComplex(Number* n)
	{
		Number_impl* num = static_cast<Number_impl*>(n);

		if (num->NUM_TYPE == Number_impl::COMPLEX_NUM_TYPE)
			return new Complex(static_cast<Complex*>(n));
		else if (num->NUM_TYPE == Number_impl::REAL_NUM_TYPE)
			return new Complex(static_cast<Real*>(n)->getValue());
		else {
			gcostringstream ost;
			ost << "Num_toolbox::toComplex -- "
				<< "unsupported type: given NUM_TYPE "
				<< num->NUM_TYPE;
#ifdef HAVE_BASIC_STRING_STREAM_TEMPLATE
			throw IllegalStateException(ost.str());
#else
			throw IllegalStateException(ost.str().c_str());
#endif
		}
	}

	Number_impl* Num_toolbox::toReal(int n)
	{
		return new Real(n);
	}	

	Number_impl* Num_toolbox::toReal(double d)
	{
		return new Real(d);
	}	

	Number_impl* Num_toolbox::toReal(Number* n)
	{
		Number_impl* num = static_cast<Number_impl*>(n);

		if (num->NUM_TYPE == Number_impl::REAL_NUM_TYPE)
			return new Complex(static_cast<Real*>(n)->getValue());
		else {
			gcostringstream ost;
			ost << "Num_toolbox::toReal -- "
				<< "unsupported type: given NUM_TYPE "
				<< num->NUM_TYPE;
#ifdef HAVE_BASIC_STRING_STREAM_TEMPLATE
			throw IllegalStateException(ost.str());
#else
			throw IllegalStateException(ost.str().c_str());
#endif
		}
	}

	Number_impl* Num_toolbox::toRational(int n, int d)
	{
		Integer_impl* numer = new SmallInteger(n);
		Integer_impl* denom = (d != 0)
			? new SmallInteger(d)
			: SmallInteger::zero();
		return new Rational(numer, denom);
	}	

	Number_impl* Num_toolbox::toRational(const ZZ& n, const ZZ& d)
	{
		Integer_impl* numer = new BigInteger(n);
		Integer_impl* denom = new BigInteger(d);
		return new Rational(numer, denom);
	}	

	Number_impl* Num_toolbox::rationalize(double _din)
	{
		RR real_input(_din);
		RR p = real_input.abs();

		RR q(1.0);

		RR r;
		ZZ ak_1;
		ZZ ak;
		ZZ tmp;

		// Precalculate a0, a1
		for (int n = 0; n < 2; n++) {
			tmp = p / q;
			if (n == 0) {
				ak_1 = tmp;
				r = p - (q * ak_1);
			} else {
				ak = tmp;
				r = p - (q * ak);
			}
			p = q;
			q = r;
			if (q.signum() == 0) {
				const gcstring& msg =
					Object::getResource("number.error.noExactRepresentation");
				throw RuntimeException(msg);
			}
		}

		ZZ pk_1(ak_1);
		ZZ pk_2(1);
		ZZ qk_1(1);

		ZZ qk_2;        // 0
		ZZ numer;       // 0
		ZZ denom;       // 0
		bool stop = false;

		// Begin rationalize
		for (;;) {
			// Recursion formula for approximation
			numer = ak * pk_1;
			numer = numer + pk_2;
			denom = ak * qk_1;
			denom = denom + qk_2;

			if (stop)
				break;

			// k := k+1
			pk_2 = pk_1;
			pk_1 = numer;
			qk_2 = qk_1;
			qk_1 = denom;

			// Advance ak (next chain fraction)
			ak_1 = ak;
			ak   = p / q;
			r    = p - q * ak;
			p    = q;
			q    = r;
			stop = q.signum() == 0;
		}

		BigInteger* bigN = new BigInteger((real_input < 0)
										  ? -numer : numer);
		BigInteger* bigD = new BigInteger(denom);

		Integer_impl* bigN2 = static_cast<Integer_impl*>(bigN->simplify());
		Integer_impl* bigD2 = static_cast<Integer_impl*>(bigD->simplify());

		return new Rational(bigN2, bigD2);
	}

	Number_impl* Num_toolbox::toSmallInteger(int n)
	{
		return new SmallInteger(n);
	}

	Number_impl* Num_toolbox::toBigInteger(int n)
	{
		return new BigInteger(n);
	}	

	Number_impl* Num_toolbox::toBigInteger(long l)
	{
		return new BigInteger(l);
	}	

	Number_impl* Num_toolbox::addBigAndConstruct(int a, int b)
	{
		return new BigInteger(ZZ(a) + ZZ(b));
	}	

	Number_impl* Num_toolbox::subBigAndConstruct(int a, int b)
	{
		return new BigInteger(ZZ(a) - ZZ(b));
	}	

	Number_impl* Num_toolbox::mulBigAndConstruct(int a, int b)
	{
		return new BigInteger(ZZ(a) * ZZ(b));
	}	

	Number_impl* Num_toolbox::powBigAndConstruct(int b, int e)
	{
		BigInteger* base = new BigInteger(b);
		BigInteger* exponent = new BigInteger(e);
		return static_cast<Number_impl*>(base->pow(exponent));
	}	
