  close(IX);
  if ($MODE eq "VOTE"){
    open(IY,">$DATA_FILE");
    foreach $toshow (@list){
      print IY "$toshow:$questions{$toshow}:$titles{$toshow}:\n";
    }
    close(IY);

#include <stdio.h>
#include <sys/time.h>

int slowfib (int n)
{
	return n <= 2 ? 1 : slowfib(n-1) + slowfib(n-2);
}

int fib (int n)
{
	static int memo[100] = {0};
	if (n <= 2)
		return 1;
	if (memo[n] == 0)
	    return	memo[n] = fib(n-1) + fib(n-2);
	else
		return memo[n];
}

void printTimes(struct timeval * start, struct timeval * finish)
{
  if (finish->tv_usec < start->tv_usec)
    {
      printf("%d seconds and %d microseconds\n",
	     (finish->tv_sec - 1) - start->tv_sec,
	     (finish->tv_usec + 1000000) - start->tv_usec);
    }
  else
    {
      printf("%d seconds and %d microseconds\n",
	     (finish->tv_sec - start->tv_sec),
	     (finish->tv_usec - start->tv_usec));
    }

}

int main (int argc, char ** argv)
{
	struct timeval start, stop;
	int i;
	int fibno;
	fibno = atoi(argv[1]);
	printf("timing slow fibonacci\n");
	gettimeofday(&start, NULL);
		printf("slowfib(%d) = %d\n", fibno, slowfib(fibno));
	gettimeofday(&stop, NULL);

	printTimes(&start, &stop);

	puts("");

	puts("timing memoized fibonacci");
	gettimeofday(&start, NULL);
	printf("fib(%d)=%u\n", fibno, fib(fibno));
	gettimeofday(&stop, NULL);
	printTimes(&start, &stop);

	return 0;
}

