void cplayer::recognizeWay()
{
	if (map[cplayer::m_ypos][cplayer::m_xpos-1]==0)
	{
		m_xpos--;
		cplayer::update=true;
	}
	else
	{
		if (map[cplayer::update==false && cplayer::m_ypos+1][cplayer::m_xpos]==0)
		{
			cplayer::moveDown();
			cplayer::update=true;
		}
		else 
		{
			if (map[cplayer::update==false && cplayer::m_ypos-1][cplayer::m_xpos]==0)
			{
				cplayer::moveUp();
				cplayer::update=true;
			}
			else
			{
				if (map[cplayer::update==false && cplayer::m_ypos][cplayer::m_xpos+1]==0)
				{				
					cplayer::moveRight();
					cplayer::update=true;
				}	
			}
		}
	}			
	cplayer::update=false;

}

void cplayer::draw()
{
	map[cplayer::m_ypos][cplayer::m_xpos]=8;
	for(int i = 0; i<10; i++)
			{
				for(int j = 0; j<10; j++)

				 {
					 cout << map[i][j];
				 }
				cout << endl;
			}

}

