-(void)showReleaseNotes:(NSArray*)versions fromVersion:(NSString*)currentVersion
{
	if (!versions or !currentVersion)
		return;
	NSData* data=[NSData dataWithContentsOfFile:[[NSBundle bundleForClass:[self class]] pathForResource:@"WhatsNew" ofType:@"html"]];
	if (!data)
		return;

	NSMutableString* page=[[NSMutableString alloc] initWithData:data encoding:NSUTF8StringEncoding];
	NSMutableString* versionString=[[NSMutableString alloc] initWithCapacity:0];

	[page replaceOccurrencesOfString:@"SYSTEM_FONT" withString:[[NSFont systemFontOfSize:12.0] familyName] options:NSCaseInsensitiveSearch range:NSMakeRange(0, [page length])];

	int i=0;
	for (i=0;i<[versions count]; i++)
	{
		NSDictionary* dict=[versions objectAtIndex:i];
		NSString* version=[dict objectForKey:@"Version"];
		NSString* dateString=[dict objectForKey:@"ReleaseDateString"];
		NSDate* releaseDate=NULL;
		NSString* notes=[dict objectForKey:@"WhatsNew"];
		NSString* formattedDate=NULL;

		NSDateFormatter* tempFormatter=[[[NSDateFormatter alloc] init] autorelease];

		if (![tempFormatter getObjectValue:&releaseDate forString:dateString errorDescription:NULL])
		{
			releaseDate=[NSDate dateWithTimeIntervalSince1970:[[dict objectForKey:@"ReleaseDate"] doubleValue]];
		}

		if (releaseDate)
		{
			CFLocaleRef locale=CFLocaleCopyCurrent();
			if (locale)
			{
				CFDateFormatterRef formatter=CFDateFormatterCreate(NULL, locale, kCFDateFormatterFullStyle, kCFDateFormatterNoStyle);

				if (formatter)
				{
					formattedDate=[(NSString*)CFDateFormatterCreateStringWithDate(NULL, formatter, (CFDateRef)releaseDate) autorelease];

					CFRelease(formatter);
				}

				CFRelease(locale);
			}
		}
		NSString* theString=[NSString stringWithFormat:@"<p class=\"VersionHeader\"><span class=\"Version\">Version %@</span> <span class=\"Date\">(%@)</span></p>\n<div class=\"LinerNotes\">%@</div><br /><br />", version, formattedDate, notes];
		[versionString appendString:theString];
	}

	[page replaceOccurrencesOfString:@"O_RLY?" withString:versionString options:NSCaseInsensitiveSearch range:NSMakeRange(0, [page length])];

	if ([versionString length])
	{
		NSData* coolData = [page dataUsingEncoding:NSUTF8StringEncoding];
//		[coolData writeToFile:[@"~/Desktop/Release.html" stringByExpandingTildeInPath] atomically:YES];
		if (coolData)
		{
			NSAttributedString* coolResult=[[NSAttributedString alloc] initWithHTML:coolData documentAttributes:NULL];
			if (coolResult)
			{
				[[releaseNotesBox textStorage] setAttributedString:coolResult];

			}
		}
	}

	[versionString release];
	[page release];
}

