class Mass
{
public:
	float mass;
	Vec3d pos, vel, force;
	Mass(float m, Vec3d pos)
	{
		this->mass = m;
		this->pos = pos;
	}
	virtual void Render()
	{
		cout<<"Rendered";
	}

	void ApplyForce(Vec3d force)
	{
		this->force+=force;
	}
	void init()
	{
		//Reset the forces for the object
		force[0] = 0;
		force[1] = 0;
		force[2] = 0;
		//Now, render the object on the screen


	}
	void simulate(float dtime)
	{
		vel += (force/mass) * dtime;
		pos += vel * dtime;

	}
};






