class Model : public Mass, public MD2Obj
{
public:
	int CurFrame;

	Model(float m, Vec3d pos) : Mass(m, pos)
	{
		CurFrame = 0;
	}

	bool LoadTGATexture(char *TexName, GLuint TexHandle)
	{
		TGAImg Img;        // Image loader

		// Load our Texture
		if(Img.Load(TexName)!=IMG_OK)
			return false;

		glBindTexture(GL_TEXTURE_2D,TexHandle); // Set our Tex handle as current

		// Create the texture
		if(Img.GetBPP()==24)
			glTexImage2D(GL_TEXTURE_2D,0,3,Img.GetWidth(),Img.GetHeight(),0,GL_RGB,GL_UNSIGNED_BYTE,Img.GetImg());
		else if(Img.GetBPP()==32)
			glTexImage2D(GL_TEXTURE_2D,0,4,Img.GetWidth(),Img.GetHeight(),0,GL_RGBA,GL_UNSIGNED_BYTE,Img.GetImg());
		else
			return false;

		// Specify filtering and edge actions
		glTexParameteri(GL_TEXTURE_2D,GL_TEXTURE_MIN_FILTER,GL_LINEAR);
		glTexParameteri(GL_TEXTURE_2D,GL_TEXTURE_MAG_FILTER,GL_LINEAR);
		glTexParameteri(GL_TEXTURE_2D,GL_TEXTURE_WRAP_S,GL_CLAMP);
		glTexParameteri(GL_TEXTURE_2D,GL_TEXTURE_WRAP_T,GL_CLAMP);

		return true;
	}
	void Render()//This function handles all the animation et al bit
	{	

		glLoadIdentity();
		glPushMatrix();

		MD2Obj Obj; // Our object class
		GLuint AnimTexture[32]; // Texture store 
		float ViewRotate=0.0f; // A few vars to handle view rotation, animation and time base values
		char Text[256];
		GLfloat Ambient[]  = { 0.1f,  0.1f,  0.1f, 1.0f};	 // Ambient light value
		GLfloat Diffuse[]  = { 1.0f,  1.0f,  1.0f, 1.0f};	 // Diffuse light value
		GLfloat Position[] = {500.0f, 0.0f, 500.0f, 1.0f};	 // Light position
		GLfloat Position1[] = {5.0f, 0.0f, 5.0f, 1.0f};	 // Light position


		// Allocate all textures in one go
		glGenTextures(32,AnimTexture);
		int RunLevel;
		// Load our Object
		if(Obj.Load("Obj\\WalkMech.md2"))
		{
			RunLevel=0;
			cout<<"Unable to load Object!\n";
			return;
		}

		// Find out how many frames we have
		int Frames=Obj.GetFrameCount();

		// Load a texture for our object
		strcpy(Text,"..\\Obj\\");
		strcat(Text,Obj.GetTexName());

		if(LoadTGATexture(Text,AnimTexture[0]))
		{
			Obj.SetTexture(AnimTexture[0]);
		}

		// Enable Lighting
		glEnable(GL_LIGHTING);
		glEnable(GL_LIGHT0);
		glEnable(GL_LIGHT1);

		glLightfv(GL_LIGHT0, GL_AMBIENT, Ambient);	// Set the ambient lighting value for Light0
		glLightfv(GL_LIGHT0, GL_DIFFUSE, Diffuse);	// Set the diffuse lighting value for Light0

		glLightfv(GL_LIGHT1, GL_AMBIENT, Ambient);	// Set the ambient lighting value for Light0
		glLightfv(GL_LIGHT1, GL_DIFFUSE, Diffuse);	// Set the diffuse lighting value for Light0

		//Make upright
		glRotatef(-60.0f,1.0f,0.0f,0.0f);

		// Set Light Position
		glLightfv(GL_LIGHT0,GL_POSITION,Position);		// Set position for the light
		glLightfv(GL_LIGHT1,GL_POSITION,Position1);		// Set position for the light

		// Rotate view
		glRotatef(ViewRotate,0.0f,0.0f,1.0f);

		//Make is smaller
		glScalef(0.3, 0.3, 0.3);
		//glPushMatrix();
		glTranslatef(pos[0], pos[1], pos[2]);
		//glTranslatef(0.0f,-15.0f,-80.0f);
		//glPopMatrix();
		// Draw our Object
		Obj.Draw(CurFrame);

		if(CurFrame>=30)
			CurFrame=0;
		else
			CurFrame+=1;

		// Show our scene
		glutSwapBuffers();
		// Clean up textures
		glDeleteTextures(32,AnimTexture);
		glPopMatrix();

	}
	//Ended render function to display the object
};


//The simulation class, which helps us simulate an object for the simulation
class Simulation
{
public:
	int num_of_masses, int num_of_models;
	Ball **	masses;
	Model ** models;

	Simulation(int num_of_masses, int num_of_models, float mass)
	{
		this->num_of_masses = num_of_masses;
		this->num_of_models = num_of_models;

		masses = new Ball*[num_of_masses];
		models = new Model*[num_of_models];

		for (int a=0; a<num_of_masses; a++)
		{
            masses[a] = new Ball(mass, Vec3d(500, 0, 500));
			models[a] = new Model(mass, Vec3d(0, 0, 0));
		}
	}
	virtual void toast()
	{
		for(int a=0; a<num_of_masses; a++)
		{
			delete masses[a];
			delete models[a];
			masses[a] = NULL;
			models[a] = NULL;
		}
		delete(masses);
		masses = NULL;
		delete(models);
		models = NULL;
	}

	Mass *getMass(int index)
	{
		if(index<0 || index>=num_of_masses)
			return NULL;
		return masses[index];
	}

	Model *getModel(int index)
	{
		if(index<0 || index>=num_of_models)
			return NULL;
		return models[index];
	}
	virtual void init()
	{
