			models[i]->init();
			models[i]->Render();
		}
	}

	virtual void solve()
	{
	}

	virtual void simulate(float dtime)
	{
		for(int i=0; i<num_of_masses; i++)
		{
			masses[i]->simulate(dtime);
			models[i]->simulate(dtime);
		}

	}

	virtual void operate(float dtime)
	{
		//The main function which calls all the relevant functions to simulate stuff
		init();
		solve();
		simulate(dtime);
	}
};

