+ (NSString *)bytesToString:(long)numBytes{
    double result = (double)numBytes;
    int level = 0;
    while((int)(result / 1024) > 0){
        result = (float)result / 1024;
        //keepGoing = YES;
        level++;
    }
    NSString *postFix;
    switch (level){
	case 0: //bytes (b)
	    postFix = [[NSString alloc] initWithString:@"b"];
	    break;
	case 1: //kilobytes (kB)
	    postFix = [[NSString alloc] initWithString:@"kB"];
	    break;
	case 2: //megabytes (MB)
	    postFix = [[NSString alloc] initWithString:@"MB"];
	    break;
	case 3: //gigabytes (GB)
	    postFix = [[NSString alloc] initWithString:@"GB"];
	    break;
	case 4: //terabytes (TB)
	    postFix = [[NSString alloc] initWithString:@"TB"];
	    break;
	case 5: //petabytes (PB)
	    postFix = [[NSString alloc] initWithString:@"PB"];
	    break;
	case 6: //exabytes (EB)
	    postFix = [[NSString alloc] initWithString:@"EB"];
	    break;
	case 7: //zettabyte (ZB)
	    postFix = [[NSString alloc] initWithString:@"ZB"];
	    break;
	case 8: //yottabyte (YB)
	    postFix = [[NSString alloc] initWithString:@"YB"];
	    break;
	default:
	    postFix = [[NSString alloc] initWithString:@"b"];
    }
    return [NSString stringWithFormat:@"%.2f %@", result, [postFix autorelease]];
}

typedef struct {
        int value;
        struct node *next;
        struct node *prev;
} node;

node * doubly_search(node *root, int value)
{
        node *node_return = NULL;

        if(root!=NULL)
        {
                if(root->value == value)
                {
                        node_return = root;
                }
                else
                {
                        if(root->next != NULL)
                        {
                                doubly_search((node *)root->next,value);
                        }
                        else
                        {
                                printf("found 3\n");
                        }
                }
        }
        return node_return;
}

