BOOL CheckCreateOutputFileStub(CFURLRef url) {

  FSRef fsRef, newfsRef;
  BOOL ret = false;

  //see if already exists
  ret = CFURLGetFSRef(url,&fsRef);

  // make the file
  if (!ret) {
    // make the file
    CFURLRef baseurl = CFURLCreateCopyDeletingLastPathComponent(kCFAllocatorDefault,url);
    ret = CFURLGetFSRef(baseurl,&fsRef);
    if (!ret)
        return ret;
    CFStringRef filename = CFURLCopyLastPathComponent(url);

    FSMakeFSRefUnicode(
      &fsRef,
      CFStringGetLength(filename),
      CFStringGetCharactersPtr(filename),
      kTextEncodingUnknown,
      &newfsRef
    );

    //see if exists now
    ret = CFURLGetFSRef(url,&newfsRef);

  } 
  return ret;
}

/*
 * Given two coordinates (x1,y1) and (x2,y2).
 * Need to check whether either the x'es or the y's differ by one (not both).
 * The implementation below works, but I'm wondering whether there's a more elegant way (faster/cleaner/...).
 */

if(x1 == x2)
{
	if(y1 == y2 + 1)
		return true;
	else if(y1 == y2 - 1)
		return true;
}
else if(y1 == y2)
{
	if(x1 == x2 + 1)
		return true;
	else if(x1 == x2 - 1)
		return true;
}

return false;

