//Set up the thread, create our libezv instance, and pass the proxy for it back to the calling thread
- (void)finishBonjourThreadPreparation:(id)ezvClient {
	threadAutoreleasePool = [[NSAutoreleasePool alloc] init];

	libezv = [[AWEzv alloc] initWithClient:ezvClient];

	bonjourThreadMessenger = [[NDRunLoopMessenger runLoopMessengerForCurrentRunLoop] retain];
	libezvProxy = [[bonjourThreadMessenger target:libezv] retain];
	bonjourThreadRunLoop = [[NSRunLoop currentRunLoop] retain];	//Create a new runloop

	//Use a timer to periodically release our autorelease pool so we don't continually grow in memory usage
	autoreleaseTimer = [[NSTimer scheduledTimerWithTimeInterval:AUTORELEASE_POOL_REFRESH
														 target:self
													   selector:@selector(refreshAutoreleasePool:)
													   userInfo:nil
														repeats:YES] retain];
	//We're good to go; release that lock
	[bonjourThreadLock unlock];

	//and start the runloop
	[bonjourThreadRunLoop run];

	//NSAssert(FALSE, @"finishBonjourThreadPreparation: CFRunLoopRun() stopped running.");
	NSLog(@"finishBonjourThreadPreparation: CFRunLoopRun() stopped running.");

	//Clean up
	[libezv release];
	[autoreleaseTimer invalidate]; [autoreleaseTimer release];
	bonjourThreadRunLoop = NULL;
	[bonjourThreadMessenger release]; bonjourThreadMessenger = nil;
	[libezvProxy release]; libezvProxy = nil;
    [threadAutoreleasePool release];

	[bonjourThreadLock unlock];
}

//Stop our runloop so we can deallocate properly
- (void)sessionManagerWillDestroySession:(id)sessionManager {
	//Stop the runloop of our thread
	//[bonjourThreadRunLoop stop];
	CFRunLoopStop([bonjourThreadRunLoop getCFRunLoop]);
	[bonjourThreadRunLoop release]; bonjourThreadRunLoop = nil;

	[bonjourThreadLock lock];
	[bonjourThreadLock release]; bonjourThreadLock = nil;

	//Let CKAbstractAccountImp do it's stuff
	[super sessionManagerWillDestroySession:sessionManager];
}

