#include <stdio.h>
#include "stack.h"

int main(void)
{
    stack s;

    memset(s.stack_data,512,0);
    s.stack_ptr = s.stack_data;
    stack_push(&s,'c');

    printf("%c\n",stack_pop(&s));

    return 0;
}

void stack_push(stack *s,char c)
{
    if(s->stack_index==512)
    {
        s->stack_ptr -= 512;
        s->stack_index = 0;
    }
    *s->stack_ptr = c;
    s->stack_ptr++;
    s->stack_index++;

}

char stack_pop(stack *s)
{
    char c_ret=0;

    s->stack_ptr--;
    s->stack_index--;

    if(s->stack_index==0)
        c_ret = s->stack_data[0];

    c_ret = *s->stack_ptr;

    return c_ret;
}

#ifndef STAK_H
#define STACK_H

typedef struct
{
    unsigned int stack_index;
    char         *stack_ptr;
    char         stack_data[512];
} stack;

char stack_pop(stack *);
void stack_push(stack *, char);

#endif

