#include <stdio.h>
#include <string.h>
#include <unistd.h>

#include <sys/types.h>
#include <sys/mman.h>

struct thread_info {
    pthread_t thread;
    void* stack;
};

static struct thread_info * volatile previous_thread_info = 0;

static void *
thread_func (struct thread_info *th)
{
  struct thread_info * new_thread_info = (struct thread_info *)malloc(sizeof(struct thread_info));
  new_thread_info->thread = pthread_self();
  new_thread_info->stack = th->stack;

  if (previous_thread_info) {
      pthread_join(previous_thread_info->thread, NULL);
      free(previous_thread_info->stack);
      free(previous_thread_info);
  }
  previous_thread_info = new_thread_info;
  return NULL;
}

#define ITERATIONS 64

static int
do_test (void)
{
    int result = 0;
    int i, res;
    pthread_attr_t attr;
    pthread_t thread_id;
    struct thread_info threads[ITERATIONS];

    for (i=0; i < ITERATIONS; ++i) {
        printf("Iteration: %d\n", i);
        threads[i].stack = malloc(1024 * PTHREAD_STACK_MIN);
        if (!threads[i].stack) {
            printf ("malloc failed %s\n", strerror (res));
            return 1;
        }

        pthread_attr_init (&attr);

        res = pthread_attr_setstack (&attr, threads[i].stack, 1024 * PTHREAD_STACK_MIN);
        if (res) {
            printf ("pthread_attr_setstack failed %d\n", res);
            result = 1;
        }

        /* Create the thread.  */
        res = pthread_create (&thread_id, &attr, (void *(*)(void *))thread_func, &threads[i]);
        if (res) {
            printf ("pthread_create failed %d\n", res);
            result = 1;
            break;
        }

        pthread_attr_destroy (&attr);
    }

    return result;
}

int main(int argc, char* argv[]) {
    do_test();
    return 0;
}

