std::string MacXApplication::DoSaveFileDialog(void)
{
	std::string path = "";

	OSStatus status;
	NavDialogCreationOptions dlgOptions;
	status = NavGetDefaultDialogCreationOptions (&dlgOptions);
    NavDialogRef navDlg;
	NavReplyRecord outReply;

	dlgOptions.modality |= kWindowModalityAppModal;

	if (noErr != (status = NavCreatePutFileDialog(&dlgOptions, 0, 0, NULL, NULL, &navDlg)))
	{
		assert(0);
	}


	if (noErr != (status = NavDialogRun(navDlg)))
	{
		NavDialogDispose(navDlg);
		assert(0);
	}

	if(NavDialogGetUserAction(navDlg) == kNavUserActionCancel || kNavUserActionNone)
	{
		return path;
	}

	if (noErr != (status = NavDialogGetReply(navDlg, &outReply)))
	{
		assert(0);
	}

	FSRef		fileRef;
	CFURLRef	urlRef;	


	// Get the file
	status = AEGetNthPtr(&(outReply.selection), 1, typeFSRef, NULL, NULL, &fileRef, sizeof(FSRef), NULL);
	assert( status == noErr);

	// Convert it to a CFURL
	urlRef = CFURLCreateFromFSRef(NULL, &fileRef);

	CFStringRef	fstring = CFURLCopyFileSystemPath(urlRef, kCFURLPOSIXPathStyle);
	int bufsize = CFStringGetLength(fstring)*2+1;
	char* buf = new char[bufsize];
	CFStringGetCString(fstring, buf, bufsize, kCFStringEncodingUTF8);

	path = buf;

	delete[] buf;
	CFRelease(fstring);

	NavDialogDispose(navDlg);

	return path;
}

